class Solution {
public:
    vector<int> shortestDistanceAfterQueries(int n, vector<vector<int>>& queries) {
              set<int>st;
              for(int i=0;i<n;i++) st.insert(i);

              vector<int>ans;



               for(auto it : queries){

                      auto x = st.lower_bound(it[0]);

                      vector<int>vec;
                      for(auto itr = x ; itr!=st.end() ; itr++){
                             int val = *itr;

                             if(val > it[0] && val < it[1]) vec.push_back(val);
                             else if(val >=it[1]) break;

                      }
                      for(auto itr : vec) {
                           if(st.count(itr)) st.erase(itr);
                      }
                     ans.push_back(st.size()-1);
               }
            return ans;
    }
};
