class Solution {
public:
    vector<int> shortestDistanceAfterQueries(int n, vector<vector<int>>& q) {
        set<int>L;
        for(int i=0;i<n;i++){
            L.insert(i);
        }
        vector<bool>st(n+10);
        vector<int>ans;
        for(auto &p:q){
            int a=p[0];
            int b=p[1];
            auto it=L.upper_bound(a);
            while(1){
                if(*it<b){
                    it=L.erase(it);
                    if(it==L.end())break;
                }else{
                    break;
                }
            }

            ans.push_back(L.size()-1);
        }
        return ans;
    }
};
