class Solution {
public:
    vector<int> shortestDistanceAfterQueries(int n, vector<vector<int>>& queries) {

           vector<int>ans ; 
        set < int > s ; 
           for(int i =0 ; i < n ; i++)s.insert(i) ; 
        int mini = n-1; 
           for(int i = 0 ; i < queries.size() ; i++){
                int u = queries[i][0] ; 
               int v = queries[i][1] ; 
               for(int i = *(s.upper_bound(u)) ; i < *(s.lower_bound(v)) ; i++){
                    if(s.find(i) != s.end())s.erase(i) ; 
               }
               ans.push_back(s.size()-1) ; 
           }
        return ans ; 
    }
};
