class Solution {
public:


    vector<int> shortestDistanceAfterQueries(int n, vector<vector<int>>& q){
        map<int,int> mp;
        int ans = n - 1;
        for(int i=0;i<n - 1;i++){
            mp[i] = 1;
        }
        vector<int> a;
        for(int i=0;i<q.size();i++){
            if(q[i][1] - q[i][0] >= mp[q[i][0]]){
                int ed = q[i][1];
                int sub = 0;
                int it = q[i][0];
                while(it < ed){
                    sub++;
                    int oit = it;
                    it = it + mp[it];
                    // cout<<it<<" ";  
                    mp[oit] = 1e9;
                    if(it == oit)   break;
                }
                // cout<<endl;
                // cout<<it<<" "<<ed<<" "<<sub<<endl;
                ans -= sub - 1;
                mp[q[i][0]] = q[i][1] - q[i][0];
            }
            a.push_back(ans);
        }
        return a;
    }
};
