from sortedcontainers import SortedList

class Solution:
    def shortestDistanceAfterQueries(self, n: int, queries: List[List[int]]) -> List[int]:
        sl = SortedList()
        short_cut = 0
        answer = []

        for u, v in queries:
            idx = sl.bisect_right((u, -v))

            if idx == 0:
                while len(sl) > idx:
                    comp = sl[idx]
                    if -comp[1] <= v:
                        sl.remove(comp)
                        short_cut -= -comp[1] - comp[0] - 1
                    else:
                        break

                sl.add((u, -v))
                short_cut += v - u - 1
            else:
                left, nr = sl[idx - 1]
                right = -nr

                if right >= v:
                    pass
                else:
                    while len(sl) > idx:
                        comp = sl[idx]
                        if -comp[1] <= v:
                            sl.remove(comp)
                            short_cut -= -comp[1] - comp[0] - 1
                        else:
                            break

                    sl.add((u, -v))
                    short_cut += v - u - 1

            answer.append(n - 1 - short_cut)

        return answer
