class Solution {
public:
    vector<int> shortestDistanceAfterQueries(int n, vector<vector<int>>& queries) {
        vector<int> results;
        vector<int> next;
        for (int i = 0; i < n; i++)
            next.push_back(i + 1);
        int result = n - 1;
        for (auto query : queries)
        {
            int x = query[0], y = query[1];
            if (next[x] < y)
            {
                int d = 0;
                while (x < y)
                {
                    d++;
                    int xx = next[x];
                    next[x] = y;
                    x = xx;
                }
                result -= (d - 1);
            }
            results.push_back(result);
        }

        return results;
    }
};
