class Solution {
    fun shortestDistanceAfterQueries(n: Int, queries: Array<IntArray>): IntArray {
        val nextArr = IntArray(n) {
            it + 1
        }
        val skipArr = BooleanArray(n)
        var curAns = n - 1
        val ans = IntArray(queries.size)
        queries.forEachIndexed { i, query ->
            val a = query[0]
            val b = query[1]
            if (!skipArr[b] && !skipArr[a]) {
                if (nextArr[a] < b) {
                    var t = a
                    var lastCost = 0
                    while (t != b) {
                        lastCost++
                        t = nextArr[t]
                    }
                    curAns -= (lastCost - 1)
                    for (k in nextArr[a] until b) {
                        skipArr[k] = true
                    }
                    nextArr[a] = b
                }
            }
            ans[i] = curAns
        }
        return ans
    }
}
