class Dsu {
public:
    int fa[100005];
    void init(int x) {
        for(int i = 0; i <= x; ++i) {
            fa[i] = i;
        }
    }
    int find(int x) {
        return x == fa[x] ? x : fa[x] = find(fa[x]);
    }
    bool merge(int x, int y) {
        x = find(x); y = find(y);
        if(x == y) return false;
        fa[x] = y;
        return true;
    }
}dsu;
class Solution {
public:
    vector<int> shortestDistanceAfterQueries(int n, vector<vector<int>>& queries) {
        int now = n - 1;
        vector<int> ans;
        ans.reserve(n);
        dsu.init(n);
        for(auto& query : queries) {
            int u = query[0], v = query[1];
            for(int w = dsu.find(u); w < v - 1; w = dsu.find(w)) {
                --now;
                dsu.fa[w] = w + 1;
            }
            ans.push_back(now);
        }
        return ans;
    }
};
