class Solution {
public:
    vector<int> shortestDistanceAfterQueries(int n, vector<vector<int>>& queries) {
        set<pair<int,int>> st;
        for(int i=0;i<n-1;++i) st.insert({i,i+1});
        int q=queries.size();
        vector<int> ans(q);
        for(int i=0;i<q;++i){
            int f=queries[i][0],s=queries[i][1];
            auto it=st.upper_bound({f,n});
            // if(it!=st.end()){
            //     //cout<<it->first<<" "<<it->second<<" ";
            // }
            if(it!=st.begin()){
                auto temp=it;temp--;
                if(temp->second>=s){
                    ans[i]=st.size();continue;
                }
            }
            if(it!=st.begin()){
                auto temp=it;temp--;
                if(temp->first==f) it--;
            }
            int mini=min(it->first,f),maxi=max(it->second,s);
            //cout<<mini<<" "<<maxi<<" maxi ";
            while(true){
                if(it==st.end() || it->first>=maxi) break;
                auto temp=it;
                maxi=max(it->second,maxi);
                it++;
                st.erase(temp);
            }
            st.insert({mini,maxi});
            ans[i]=st.size();
        }
        //cout<<endl;
        return ans;
    }
};
