from sortedcontainers import SortedList

class Solution:
    def shortestDistanceAfterQueries(self, n: int, queries: List[List[int]]) -> List[int]:
        tl = n - 1
        sl = SortedList(list(range(n - 1)))
        res = []
        for u, v in queries:
            a, b = sl.bisect_left(u + 1), sl.bisect_left(v)
            to_remove = []
            for i in range(a, b):
                to_remove.append(sl[i])
            for el in to_remove:
                sl.remove(el)
            res.append(len(sl))
        return res
