from sortedcontainers import SortedList
class Solution:
    def shortestDistanceAfterQueries(self, n: int, queries: List[List[int]]) -> List[int]:
        arr = SortedList([[i, i+1] for i in range(n)])        
        # print(arr)
        ans = [0] * len(queries)
        for i, (a, b) in enumerate(queries):
            j = bisect_left(arr, a, key = lambda x:x[0])
            # print(j)
            if arr[j][0] == a and arr[j][1] < b:
                while arr[j][0] != b:
                    arr.pop(j)
                arr.add([a, b])
            ans[i] = len(arr) - 1
            # print(arr)
        return ans
