class Solution {
public:
    vector<int> shortestDistanceAfterQueries(int n, vector<vector<int>>& queries) {
        set<pair<int,int>> I;
        for (int i = 0; i < n-1; i++)
            I.insert({i, i+1});

        vector<int> ret;
        int ans = n - 1;
        for (const auto &e : queries) {
            int u = e[0], v = e[1];

            auto it = I.lower_bound({u, -1});
            vector<pair<int,int>> RM;
            int org = 0;
            for (; it != I.end(); ++it) {
                auto [x, y] = *it;
                if (x >= v) break;
                RM.push_back({x, y});
                org += 1;
            }
            for (auto p : RM) I.erase(p);
            if (!RM.empty()) {
                I.insert({u, v});
                ans -= org - 1;
            }
            ret.push_back(ans);
        }
        return ret;
    }
};
