class Solution {
public:
    vector<int> shortestDistanceAfterQueries(int n, vector<vector<int>>& queries) {
        int q = queries.size();
        set<int> st;

        vector<int> ans(q,0);
        for(int i = 0; i < n ; i++) st.insert(i);

        for(int i = 0; i < q ; i++) {
            int u = queries[i][0], v = queries[i][1];

            auto it = st.lower_bound(u+1);
            vector<int> temp;
            while(it != st.end() && *it < v) {
                temp.push_back(*it);
                it++;
            }

            for(int i = 0; i < temp.size() ; i++) st.erase(temp[i]);
            // cout<<"\n";
            ans[i] = st.size()-1;
        }
        return ans;
    }
};
