class Solution {
public:
    vector<int> shortestDistanceAfterQueries(int n, vector<vector<int>>& queries) {
        set<int> st;
        for (int i = 0; i < n; i++) {
            st.insert(i);
        }
        int q = queries.size();
        vector<int> ans(q);
        for (int i = 0; i < q; i++) {
            int l = queries[i][0], r = queries[i][1];
            auto it = st.lower_bound(l + 1);
            set<int> del;
            while (it != st.end()) {
                if (*it > r - 1) {
                    break;
                }
                del.insert(*it);
                it++;
            }
            for (auto num: del) {
                st.erase(num);
            }
            ans[i] = (st.size() - 1);
        }
        return ans;
    }
};
