class Solution {
  public int[] shortestDistanceAfterQueries(int n, int[][] queries) {
    var next = new int[n];
    var dnc = new boolean[n]; // do not consider
    for (var i = 0; i < n; i++) {
      next[i] = i + 1;
    }
    var steps = n - 1;
    var ret = new int[queries.length];
    for (var i = 0; i < queries.length; i++) {
      var query = queries[i];
      var start = query[0];
      var stop = query[1];
      if (dnc[start] || stop < next[start]) {
        ret[i] = steps;
        continue;
      }
      var onext = next[start];
      next[start] = stop;
      while (onext != stop) {
        dnc[onext] = true;
        onext = next[onext];
        steps -= 1;
      }
      ret[i] = steps;
    }
    return ret;
  }
}
