#include <bits/stdc++.h>
using namespace std;

class Solution {
public:
    vector<int> shortestDistanceAfterQueries(int n, vector<vector<int>>& queries) {
        set<pair<int, int>> edges;
        for (int i = 0; i < n - 1; i++) {
            edges.emplace(i, i + 1);
        }
        edges.emplace(n - 1, INT_MAX);

        int m = queries.size();
        vector<int> ans(m);
        for (int i = 0; i < m; i++) {
            // cout << i << endl;
            int fr = queries[i][0], to = queries[i][1];
            auto itb = edges.lower_bound({fr, -1});
            auto ite = edges.lower_bound({to, -1});

            // cout << itb->first << " " << itb->second << endl;
            // cout << ite->first << " " << ite->second << endl;

            if (itb->first != fr) {
                ans[i] = ans[i - 1];
                continue;
            }
            edges.erase(itb, ite);
            edges.emplace(fr, to);
            // cout << "---------------" << endl;
            // for (auto p : edges) {
            //     cout << p.first << " " << p.second << endl;
            // }
            // cout << "----------" << endl;
            ans[i] = edges.size() - 1;
        }
        return ans;
    }
};
