class Solution {
public:
    vector<int> shortestDistanceAfterQueries(int n, vector<vector<int>>& q) {
        int m = q.size();

        set<pair<int, int>> st;

        for(int i=0; i+1<n; i++) {
            st.insert({i, i+1});
        }

        vector<int> a(m);

        for(int i=0; i<m; i++) {
            int u = q[i][0], v = q[i][1];
            auto itr = st.lower_bound({u, -1});

            if(itr == st.end()) {
                a[i] = st.size();
                continue;
            }

            bool toAdd = false;
            while(itr != st.end() && v >= itr->second) {
                auto nxt = itr;
                nxt++;
                st.erase(itr);
                itr = nxt;
                toAdd = true;
            }

            if(toAdd) {
                st.insert({u, v});
            }

            a[i] = st.size();
        }

        return a;
    }
};
