class Solution {
public:
    vector<int> shortestDistanceAfterQueries(int n, vector<vector<int>>& queries) {
        vector<int> next(n);

        for (int i = 0; i < n; i++)
            next[i] = i + 1;

        vector<int> ans;
        int dist = n - 1;

        for (auto &q: queries) {
            auto [x, y] = tie(q[0], q[1]);
            int count = 0;

            int cur = x;

            while (cur != y) {
                int jump = next[cur];
                next[cur] = max(next[cur], y);
                cur = jump;
                count++;

                if(cur > y) {
                    count = 1;
                    break;
                }
            }

            next[x] = max(next[x], y);

            dist = dist - count + 1;
            //cout << count << ' ' << dist << endl;

            ans.push_back(dist);
        }

        return ans;
    }
};
