class Link:
    def __init__(self, val: int):
        self.val = val
        self.next = None
        self.valid = True

class Solution:
    def shortestDistanceAfterQueries(self, n: int, queries: List[List[int]]) -> List[int]:
        cities = [Link(i) for i in range(n)]
        for i in range(1, n):
            cities[i - 1].next = cities[i]

        curr_dist = n - 1
        results = []
        for start, end in queries:
            if cities[start].valid and cities[start].next.val < cities[end].val:
                dist_saved = 0
                curr = cities[start].next
                while curr != cities[end]:
                    curr.valid = False
                    dist_saved += 1
                    curr = curr.next
                cities[start].next = cities[end]
                curr_dist -= dist_saved
            results.append(curr_dist)
        return results
