class Solution {
public:
    vector<int> shortestDistanceAfterQueries(int n, vector<vector<int>>& queries) {
        int q = queries.size();
        set<int> s;
        for (int i = 0; i < n; ++i) {
            s.insert(i);
        }
        vector<int> ans(q);
        for (int i = 0; i < q; ++i) {
            int u = queries[i][0], v = queries[i][1];
            s.erase(s.upper_bound(u), s.lower_bound(v));
            ans[i] = s.size() - 1;
        }
        return ans;
    }
};
