class Solution {
public:
    vector<int> shortestDistanceAfterQueries(int n, vector<vector<int>>& qs) {
        set<int> st;
        for(int i = 0; i < n; ++i) {
            st.insert(i);
        }

        int m = qs.size();
        vector<int> res(m);
        for(int i = 0; i < m; ++i) {
            int u = qs[i][0], v = qs[i][1];
            auto it = st.upper_bound(u);
            while(*it < v) {
                st.erase(it++);
            }
            res[i] = st.size() - 1;
        }
        return res;

    }
};
