class Solution {
public:
    vector<int> shortestDistanceAfterQueries(int n, vector<vector<int>>& queries) {
        vector<int> res;
        res.reserve(queries.size());
        set<int> s;
        for (int i = 0; i < n; ++i) s.insert(i);
        for(auto& q : queries) {
            const int u = q[0], v = q[1];
            auto a = s.upper_bound(u);
            auto b = s.lower_bound(v);
            s.erase(a, b);
            // for (int j = 0; j < n; ++j) printf("%d ", d[j]); puts("");
            res.push_back(s.size() - 1);
        }
        return res;
    }
};
