class Solution {
public:
    vector<int> shortestDistanceAfterQueries(int n, vector<vector<int>>& queries) {
        set<vector<int>> s;

        for(int i=0;i<n-1;++i)
            s.insert(vector<int>{i,i+1});

        vector<int> ans;
        int curr = n-1;

        for(auto &q: queries){
            int u=q[0],v=q[1];
            auto it=s.lower_bound(vector<int>{u,0});

            int cnt=0;

            for(;it!=s.end() && (*it)[1]<=v;){
                auto new_it = next(it);
                s.erase(it);
                it = new_it;
                ++cnt;
            }

            if(cnt)
                s.insert(vector<int>{u,v});

            if(cnt)
                curr -= (cnt-1);

            ans.push_back(curr);
        }

        return ans;
    }
};
