class Solution {
public:
    vector<int> shortestDistanceAfterQueries(int n, vector<vector<int>>& queries) {
        set<pair<int,int>> mp;
        for(int i=1;i<n;++i){
            mp.emplace(i-1,i);
        }
        vector<int> ans;
        for(auto query: queries){
            int x = query[0], y = query[1],ok=0;
            for(;true;){
                auto it = mp.lower_bound(make_pair(x,0));
                if(it != mp.end() && (x <= (*it).first && (*it).second <=y)){
                    mp.erase(it);
                    ok=1;
                } else {
                    break;
                }
            }
            if(ok) mp.emplace(x,y);
            ans.push_back((int)mp.size());
        }
        return ans;
    }
};
