from sortedcontainers import SortedList

class Solution:
    def shortestDistanceAfterQueries(self, n: int, queries: List[List[int]]) -> List[int]:
        ans = []
        sl = SortedList(list(range(n)))
        for x, y in queries:
            l, r = sl.bisect_left(x + 1), sl.bisect_right(y - 1)
            del sl[l : r]
            ans.append(len(sl) - 1)
        return ans
