class Solution {
public:
    vector<int> shortestDistanceAfterQueries(int n, vector<vector<int>>& queries) {
        map<int, int> m;
        for (int i = 1; i < n; i++) {
            m[i - 1] = i;
        }
        vector<int> res;
        for (auto& q : queries) {
            int l = q[0], r = q[1];
            auto it = m.lower_bound(l);
            if (it == m.end() || it->first >= r || it->first == l && it->second >= r) {
                res.push_back(m.size());
            }
            else {
                while (it != m.end() && it->second <= r) {
                    it = m.erase(it);
                }
                m[l] = r;
                // for (auto p : m) cout << p.first <<" : "<< p.second<<", ";cout <<endl;
                res.push_back(m.size());
            }


        }
        return res;
    }
};
