class Solution {
public:
    vector<int> shortestDistanceAfterQueries(int n, vector<vector<int>>& queries) {
        set<int> si;
        for(int i=0;i<n;++i)
        {
            si.insert(i);
        }
        vector<int> ans;
        ans.reserve(queries.size());
        for(vector<int> &q:queries)
        {
            for(auto it=si.upper_bound(q[0]);it!=si.end() && *it<q[1];)
            {
                si.erase(it++);
            }
            ans.push_back((int)si.size()-1);
        }
        return ans;
    }
};
