class Solution {
public:
    vector<int> shortestDistanceAfterQueries(int n, vector<vector<int>>& queries) {
        set<int> path;
        for (int i = 0; i < n; i++)
            path.insert(i);
        vector<int> res;
        for (auto &q : queries){
            auto it_start = path.find(q[0]);
            auto it_end = path.find(q[1]);
            if (it_start != path.end() && it_end != path.end())
                path.erase(++it_start, it_end);
            res.push_back(path.size() - 1);
        }
        return res;
    }
};
