template<class T> struct BIT {
    int n;
    vector<T> w;
    BIT(int n) : n(n), w(n + 1) {}
    BIT(int n, vector<T> &in) : n(n), w(n + 1) {
        for(int i = 1; i <= n; i++) {
            add(i, in[i]);
        }
    }
    void add(int x, T v) {
        for(; x <= n; x += x & -x) {
            w[x] += v;
        }
    }
    T get(int x) {
        T ans = 0;
        for(; x; x -= x & -x) {
            ans += w[x];
        }
        return ans;
    }
    T get(int l, int r) {
        return get(r) - get(l - 1);
    }
};


class Solution {
public:
    vector<int> shortestDistanceAfterQueries(int n, vector<vector<int>>& q) {
        set<int> s;
        for(int i = 0; i < n; i++) s.insert(i);
        vector<int> res;
        for(int i = 0; i < q.size(); i++){
            int a=q[i][0];
            int b=q[i][1];
            auto t = s.upper_bound(a);
            while(true){
                if(*t < b){
                    t = s.erase(t);
                    if(t == s.end()) break;
                }else break;
            }
            res.push_back(s.size() - 1);
        }


        return res;
    }
};
