class Solution {
public:
    vector<int> shortestDistanceAfterQueries(int n, vector<vector<int>>& queries) {
        set<int> set;

        for (int i = 0; i < n; i++) {
            set.insert(i);
        }

        vector<int> ans;

        for (const auto& query : queries) {
            if (set.contains(query[0]) && set.contains(query[1])) {
                for (auto it = ++set.lower_bound(query[0]); *it != query[1]; it) {                
                    it = set.erase(it);
                }
            }

            ans.push_back(set.size() - 1);
        }

        return ans;
    }
};
