class Solution {
public:
    class node
    {
        public:
        int val;
        node* next;

        node(int x)
        {
            val = x;
            next = NULL;
        }
    };

    vector<int> shortestDistanceAfterQueries(int n, vector<vector<int>>& queries) {
        int cnt = n-1;

        map<int,node*> mpp;

        node* head = new node(-1);
        mpp[-1] = head;

        node* temp = head;

        for(int i=0;i<n;i++)
        {
            node* nn = new node(i);
            mpp[i] = nn;

            temp->next = nn;
            temp = nn;
        }

        // vector<int> vis(n,false);

        vector<int> ans;

        for(auto e:queries)
        {
            int u = e[0];
            int v = e[1];

            node* p = mpp[u];
            node* q = mpp[v];

            int x = 0;
            if(p)
            {
                p = p->next;
                while(p && q && p != q)
                {
                    x++;
                    mpp[p->val] = NULL;
                    // cout << p->val << " ";
                    p = p->next;
                }
                // cout << endl;

                mpp[u]->next = mpp[v];

                // cout << u << " " << v << " " << x << endl;
                cnt -= x;
            }


            ans.push_back(cnt);
        }

        return ans;
    }
};
