class Solution {
public:
    vector<int> shortestDistanceAfterQueries(int n, vector<vector<int>>& queries) {
        set<pair<int, int>> s;
        int sum = 0;

        vector<int> ans;
        for (auto v : queries) {
            int a = v[0], b = v[1];
            auto it = s.upper_bound({a, n + 1});
            bool bad = 0;
            if (it != s.begin()) {
                --it;
                if (it->second >= b) {
                    bad = 1;
                }
            }

            if (bad) {
                ans.push_back(n - 1 - sum);
                continue;
            }

            it = s.lower_bound({a, -1});
            while (it != s.end() && it->first < b) {
                sum -= it->second - it->first - 1;
                s.erase(it);
                it = s.lower_bound({a, -1});
            }
            s.insert({a, b});
            sum += b - a - 1;

            ans.push_back(n - 1 - sum);
        }
        return ans;

        return ans;
    }
};
