class Solution {
public:
    vector<int> shortestDistanceAfterQueries(int n, vector<vector<int>>& queries) {
        map<int, int> mp;
        for (int i = 0; i <= n - 2; i++) {
            mp[i] = i + 1;
        }
        vector<int> ans;
        for (auto& a : queries) {
            int l = a[0], r = a[1];
            auto it = mp.lower_bound(l);
            if (it != mp.end() && it->first == l && it->second < r) {
                vector<int> erase;
                while (it != mp.end() && it->second <= r) {
                    erase.push_back(it->first);
                    it++;
                }
                for (int i : erase) mp.erase(i);
                mp[l] = r;
            }
            //for (auto& a: mp) {
                //cout << a.first << " " << a.second << "     ";
            //}
            //cout << endl;
            ans.push_back(mp.size());
        }
        return ans;
    }
};
