from sortedcontainers import SortedList
class Solution:
    def shortestDistanceAfterQueries(self, n: int, queries: List[List[int]]) -> List[int]:
        A = SortedList(list(range(n)))
        ans = []
        for x,y in queries:
            i, j = A.bisect_left(x), A.bisect_left(y)
            #print(i,j)
            if A[i] > x:
                i+=1
            if A[j] > y:
                j -= 1
            #print(A,x,y,i,j)
            for k in range(i+1,j):
                A.__delitem__(i+1)
                #print("deleting ",k)
                #print(A)
            ans.append(len(A)-1)
        return ans
