class Solution:
    def shortestDistanceAfterQueries(self, n: int, queries: List[List[int]]) -> List[int]:
        dist = n - 1
        ans = []
        adj = {}
        for i in range(n):
            adj[i] = i + 1
        for q in queries:
            if q[0] not in adj or adj[q[0]] >= q[1]:
                ans.append(dist)
                continue
            for i in range(adj[q[0]], q[1]):
                if i in adj:
                    del adj[i]
                    dist -= 1
            adj[q[0]] = q[1]
            ans.append(dist)
        return ans
