class Solution {
    public int[] shortestDistanceAfterQueries(int n, int[][] queries) {
        int res[] = new int[queries.length];
        int dist = n-1, itr = 0;
        TreeSet<Integer> set = new TreeSet<>();
        for(int i=0;i<n;i++) set.add(i);
        for(int q[]: queries) {
            int u = q[0], v = q[1];
            Integer val = 0;
            while((val = set.higher(u)) != null && val < v) {
                set.remove(val);
            }
            res[itr++] = set.size() - 1;
        }
        return res;        
    }
}
