class Solution {
public:
    vector<int> shortestDistanceAfterQueries(int n, vector<vector<int>>& queries) {
        std::vector<int> ans, next;
        int res = n - 1;
        for (int i = 0; i < n - 1; i++) {
            next.push_back(i + 1);
        }
        std::unordered_set<int> removed;
        for (auto query : queries) {
            int x = query[0], y = query[1];
            if (removed.count(x) || removed.count(y)) {
                ans.push_back(res);
                continue;
            }
            int p = next[x];
            while (p != y) {
                removed.insert(p);
                res--;
                p = next[p];
            }
            next[x] = y;
            ans.push_back(res);
        }
        return ans;
    }
};
