class Solution {
public:
    vector<int> shortestDistanceAfterQueries(int n, vector<vector<int>>& queries) {
        set<int> st;
        for(int i = 0;i<n;i++)st.insert(i);
        vector<int> ans;
        for(auto &i:queries){
            int a = i[0],b = i[1];
            auto it = st.upper_bound(a);
            while(it != st.end()&&*it<b){
                st.erase(it);
                it = st.upper_bound(a);
            }
            ans.push_back(st.size()-1);
        }
        return ans;
    }
};
