class Solution {
public:
    vector<int> shortestDistanceAfterQueries(int n, vector<vector<int>>& queries) {

        set<int> se;
        for (int i = 0; i < n; i ++ ){
            se.insert(i);
        }

        vector<int> ans;
        for (auto i : queries) {
            int l = i[0], r = i[1];

            while (1) {
                auto x = se.upper_bound(l);
                if (x != se.end() && *x < r) {
                    // cout << *x << endl;
                    se.erase(x);
                }
                else {
                    break;
                }
            }

            ans.push_back(se.size() - 1);
        }

        return ans;
    }
};
