class Solution:
    def shortestDistanceAfterQueries(self, n: int, queries: List[List[int]]) -> List[int]:
        from sortedcontainers import SortedList
        to = SortedList([x for x in range(n)])

        ans = []

        for x,y in queries:
            ind = to.bisect_left(x)
            if to[ind] == x:
                ind+=1

                while to[ind]<y:
                    to.discard(to[ind])
            ans.append(len(to)-1)
        return ans
