public:
    vector<int> shortestDistanceAfterQueries(int n, vector<vector<int>>& queries) {

        int m = queries.size();
        vector<int> res(m);

        set<pair<int, int>> S;
        for (int i = 0; i + 1 < n; i++) {
            S.emplace(i, i);
        }

        display(S);

        for (int it = 0; it < m; it++) {
            int u = queries[it][0], v = queries[it][1] - 1;

            auto IT = S.lower_bound({u, -1});
            if (IT != S.end() && (*IT).second <= v) {
                vector<pair<int,int>> temp;
                while (IT != S.end() && (*IT).second <= v) {
                    temp.emplace_back((*IT).first, (*IT).second);
                    IT++;
                }
                for (auto &p: temp) {
                    S.erase({p.first, p.second});
                }
                S.emplace(u, v);
            }
            display(S);
            res[it] = S.size();
        }
        return res;
    }
};
