class Solution {
public:
    vector<int> shortestDistanceAfterQueries(int n, vector<vector<int>>& queries) {
        set <int> s;
        for (int i = 1; i < n; ++i){
            s.insert(i);
        }
        vector <int> ans(queries.size());
        for (int i = 0; i < queries.size(); ++i){
            int u = queries[i][0];
            int v = queries[i][1];
            if (u > v) swap(u, v);
            while(true){
                auto it = s.upper_bound(u);
                if (it == s.end() || *it >= v) break;
                s.erase(it);
            }
            ans[i] = s.size();
        }
        return ans;
    }
};
