class Solution {
    public int[] shortestDistanceAfterQueries(int n, int[][] queries) {
        TreeSet<Integer> set = new TreeSet<>();
        for (int i = 0; i < n; i++) {
            set.add(i);
        }
        int m = queries.length;
        int[] ans = new int[m];
        for (int i = 0; i < m; i++) {
            int u = queries[i][0], v = queries[i][1];
            while (true){
                int t = set.higher(u);
                if (t >= v) break;
                set.remove(t);
            }
            ans[i] = set.size() - 1;
        }
        return ans;
    }
}
