class Solution {
public:
    vector<int> shortestDistanceAfterQueries(int n, vector<vector<int>>& queries) {
        vector<int> ans;
        set<int> st;
        for(int i = 0; i < n; i++) st.insert(i);
        for(auto it : queries){
            int x = it[0], y = it[1];
            auto tmp = st.upper_bound(x);
            while(*tmp < y){
                auto newtmp = tmp;
                tmp++;
                st.erase(newtmp);
            }
            ans.push_back(st.size() - 1);
        }   
        return ans;
    }
};
