class Solution {
public:
    vector<int> shortestDistanceAfterQueries(int n, vector<vector<int>>& queries) {
        map<int, int> mp;
        for (int i = 0; i + 1 < n; i++) {
            mp[i] = i + 1;
        }

        vector<int> ans;
        for (auto &q : queries) {
            int x = q[0], y = q[1];
            auto it = mp.find(x);
            if (it != mp.end()) {
                while (it != mp.end() && it->first < y) {
                    //std::cout << it->first << std::endl;
                    it = mp.erase(it);
                    //std::cout << it->first << std::endl;
                }

                mp[x] = y;
            }

            std::cout << mp.size() << std::endl;
            ans.push_back(mp.size());
        }

        return ans;
    }
};
