class Solution {
public:
    vector<int> shortestDistanceAfterQueries(int n, vector<vector<int>>& queries) {
        vector<int>v;
        set<int>s;
        for(int i=0;i<n-1;i++){
            s.insert(i);
        }
        s.insert(n-1);
        int ans=n-1;
        for(int i=0;i<queries.size();i++){
            int x=queries[i][0];
            int y=queries[i][1];
            int flag=0;
            while(1){
                auto it=s.lower_bound(x);
                //cout<<*it<<endl;
                if(*it >= y) break;

                flag=1;
                ans--;
                s.erase(it);
            }
            if(flag){
                ans+=1;
                s.insert(x);
            }
            v.push_back(ans);
        }
        return v;
    }
};
