from sortedcontainers import SortedList

class Solution:
    def shortestDistanceAfterQueries(self, n: int, queries: List[List[int]]) -> List[int]:
        dp = SortedList([i for i in range(n)])
        ans = []
        r = n - 1
        for (u, v) in queries:
            x = dp.bisect_right(u)
            while x < len(dp) and dp[x] < v:
                dp.remove(dp[x])
                r -= 1
            ans.append(r)
        return ans
