class Solution {
public:
    vector<int> shortestDistanceAfterQueries(int n, vector<vector<int>>& queries) {
        vector<int> ans;
        set<int> s;
        for(int i=0;i<n;++i){
            s.insert(i);
        }
        for(auto q:queries){
            auto begin = s.lower_bound(q[0]);
            auto end = s.lower_bound(q[1])--;

            for (set<int>::iterator it = begin; it != end;){
                if(*it == q[0]){
                    it ++;
                    continue;
                }
                it = s.erase(it);
            }
            ans.push_back(s.size()-1);
        }
        return ans;
    }
};
