class Solution {
    public int[] shortestDistanceAfterQueries(int n, int[][] queries) {
        TreeMap<Integer, Integer> edges = new TreeMap<>();
        for (int i = 0; i < n - 1; i++) {
            edges.put(i, i + 1);
        }
        int m = queries.length;
        int[] ans = new int[m];
        for (int i = 0; i < m; i++) {
            int u = queries[i][0], v = queries[i][1];
            if (u < v) {
                edges.subMap(u, false, v, false).clear();
            }
            ans[i] = edges.size();
        }
        return ans;
    }
}
