class Solution {
public:

    void fun(std::set<int>& s, int i, int j) {
    auto it = s.upper_bound(i);
    while (it != s.end() && *it < j) {
        it = s.erase(it);
    }
}

    vector<int> shortestDistanceAfterQueries(int n, vector<vector<int>>& queries) {
        set<int> s;
        for(int i = 0; i< n; i++){
            s.insert(i);
        }
        vector<int> answer;

        for (const auto& query : queries) {
            int ui = query[0];
            int vi = query[1];
            fun(s,ui, vi);
            answer.push_back(s.size()-1);
        }

        return answer;
    }
};
